mapper.select:
select ID, student_id, name, qq, career, data, school, link, declaration, brother,
create_at, update_at
from student where name LIKE CONCAT('%',#{name},'%')

select ID as id,student_id as studentId,name,qq,career,data,school,link,declaration,brother,create_at as createAt,update_at as updateAt from student

  ID=#{id}
  and student_id=#{studentId}
  and name=#{name}
  and qq=#{qq}
  and career = #{career}
  and data = #{data}
  and school = #{school}
  and link = #{link}
  and declaration = #{declaration}
  and brother = #{brother}
  and create_at = #{createAt}
  and update_at = #{updateAt}
